<template>
    <div class="checkbox">
        <v-checkbox
        v-model="checkboxValue"
        color="#2D7FBC"
        @change="checkboxUpdated(checkboxValue)"
        ></v-checkbox>
    </div>
 </template>    
 
 <script>
import { ref } from 'vue';
 
 export default {
   props: {
    
   },
  
   setup(props,{emit}) {
        const checkboxValue = ref(false);
        return {
            checkboxValue,
            checkboxUpdated(checkboxValue) {
              emit('chechboxClicked',checkboxValue)
          }
        }
    }
}
 </script>
 <style lang="scss">
@import "../styles/variables.scss";

.checkbox .mdi-checkbox-blank-outline {
    &::before {
        content: "\F0130" !important;
    }

}
.checkbox .mdi-checkbox-marked {
    &::before {
        content: "\F0133" !important;
    }
}

